@SQL=SELECT a.Last_Employee_ID AS C_BPartner_ID FROM A_Asset_Delivery a JOIN C_BPartner b ON b.C_BPartner_ID = a.Last_Employee_ID WHERE a.A_Asset_ID = @A_Asset_ID@ AND a.AD_Org_ID = 50006 AND b.IsEmployee = 'Y' AND EXISTS (     SELECT 1     FROM A_Asset_Delivery at     WHERE at.Last_Employee_ID = b.C_BPartner_ID     AND at.A_Asset_ID = @A_Asset_ID@     AND at.MovementDate = (         SELECT MAX(at2.MovementDate)         FROM A_Asset_Delivery at2         WHERE at2.A_Asset_ID = @A_Asset_ID@     ) )